import { useState, useEffect } from "react";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import React from "react";

function Filter({ setFilterQuery }) {
  const [fileType, setFileType] = useState("");
  const [minSize, setMinSize] = useState("");
  const [maxSize, setMaxSize] = useState("");
  const [sampletype, setSampleType] = useState("");
  const [refGenome, setRefGenome] = useState("");

  var selectedFilter = `${fileType ? `&filetype=${fileType}` : ""}`;

  useEffect(() => {
    setFilterQuery(selectedFilter);
  }, [setFilterQuery, selectedFilter]);

  return (
    <form className="search-form">
      <Select
        labelId="demo-simple-select-label"
        id="demo-simple-select"
        value={fileType}
        onChange={e => setFileType(e.target.value)}
      >
          <MenuItem value={"tdf"}>tdf</MenuItem>
          <MenuItem value={"tar"}>tar</MenuItem>
          <MenuItem value={"bam"}>bam</MenuItem>
          <MenuItem value={"bai"}>bai</MenuItem>
          <MenuItem value={"fastq"}>fastq</MenuItem>
          <MenuItem value={6}>json</MenuItem>
          <MenuItem value={7}>vcf</MenuItem>
          <MenuItem value={8}>gvcf</MenuItem>
          <MenuItem value={9}>metrics</MenuItem>
          <MenuItem value={10}>hist</MenuItem>
          <MenuItem value={11}>stats</MenuItem>
          <MenuItem value={12}>flagstats</MenuItem>
          <MenuItem value={13}>idxstats</MenuItem>
          <MenuItem value={14}>DLERROR</MenuItem>
      </Select>
    </form>
  );
}

export default Filter;
