  const [url, setUrl] = useState("");
  const [query, setQuery] = useState("");
  const [filter, setFilter] = useState("");

  function setApiUrl() {
    setUrl(query + filter);
  }

  return (
    <>
      <Search setSearchQuery={setQuery}></Search>
      <Filter setFilterQuery={setFilter}></Filter>

      {url}
    </>
  );
}

export default App;
