import { useState } from "react";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import React from "react";

function Search() {
  const [query, setQuery] = useState("");
  const [queryType, setQueryType] = useState("");

  var option;

  const setOption = event => {
    setQueryType(event.target.value);
  };

  var selected_query = "";

  if (option === 1) {
    selected_query = `patient_id=${query}`;
  } else if (option === 2) {
    selected_query = `sample_id=${query}`;
  } else {
    selected_query = `filename=${query}`;
  }

  console.log(selected_query);

  return (
    <form className="search-form">
      <div className="input-form">
        <input
          type="text"
          placeholder="Query"
          className="search-bar"
          value={query}
          onChange={e => setQuery(e.target.value)}
        />

        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={option}
          onChange={setOption}
        >
          <MenuItem value={1}>Patient ID</MenuItem>
          <MenuItem value={2}>Sample ID</MenuItem>
          <MenuItem value={3}>FileName</MenuItem>
        </Select>
      </div>
    </form>
  );
}

export default Search;
