import { useState } from "react";
import Select from "@material-ui/core/Select";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import React from "react";

const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing(2)
  }
}));

const SearchBar3 = ({ onSearch }) => {
  const [text, setText] = useState("");
  const [fileType, setFileType] = useState("");
  const [minSize, setMinSize] = useState("");
  const [maxSize, setMaxSize] = useState("");
  const [sampleType, setSampleType] = useState("");
  const [refGenome, setRefGenome] = useState("");

  const classes = useStyles();
  const [age, setAge] = React.useState("");

  const handleChange = event => {
    setAge(event.target.value);
  };

  const onSubmit = e => {
    e.preventDefault();

    if (!text) {
      alert("Please enter a query");
      return;
    }

    // var url = "http://20.191.211.229:5000/v1/datafiles?patient_id=" + patientID + `${filetype ? `&filetype=${filetype}`: ""}`
    // + `${minSize ? `&minsize=${minSize}`: ""}` + `${maxSize ? `&maxsize=${maxSize}`: ""}`
    // + `${sampleType ? `&sampletype=${sampleType}`: ""}` + "&show_metadata=true";

    // var url = `${patientID ? "paitentID" :  "sampleID"}` + `${text}`;
    // console.log(`${filetype ? `filetype=${filetype}`: ""}`)
    var selected_query = "";

    if (age === 1) {
      selected_query += "patient_id=";
    } else if (age === 2) {
      selected_query += "sample_id=";
    } else {
      url += "filename=";
    }

    var url =
      `${fileType ? `&filetype=${fileType}` : ""}` +
      `${minSize ? `&minsize=${minSize}` : ""}` +
      `${maxSize ? `&maxsize=${maxSize}` : ""}` +
      `${sampleType ? `&sampletype=${sampleType}` : ""}` +
      `${refGenome ? `&refgenome=${refGenome}` : ""}`;

    url += text;
    onSearch(url);
  };

  return (
    <form className="search-form" onSubmit={onSubmit}>
      <div className="input-form">
        <input
          type="text"
          placeholder="Query"
          className="search-bar"
          value={text}
          onChange={e => setText(e.target.value)}
        />

        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={age}
          onChange={handleChange}
        >
          <MenuItem value={1}>PatientID</MenuItem>
          <MenuItem value={2}>SampleID</MenuItem>
          <MenuItem value={3}>FileName</MenuItem>
        </Select>
      </div>
      <div className="filter-form">
        <input
          type="text"
          placeholder="File type"
          value={fileType}
          onChange={e => setFileType(e.target.value)}
        />

        <input
          type="text"
          placeholder="Min size"
          value={minSize}
          onChange={e => setMinSize(e.target.value)}
        />

        <input
          type="text"
          placeholder="Max size"
          value={maxSize}
          onChange={e => setMaxSize(e.target.value)}
        />

        <input
          type="text"
          placeholder="Sample type"
          value={sampleType}
          onChange={e => setSampleType(e.target.value)}
        />

        <input
          type="text"
          placeholder="Genome"
          value={refGenome}
          onChange={e => setRefGenome(e.target.value)}
        />

        <input type="submit" value="Search" className="btn btn-block" />
      </div>
    </form>
  );
};

export default SearchBar3;
